import React, { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

import AnalysisImage from '../assets/Intuitive Analytics picotgram.svg';
import CustomizableImage from '../assets/Customizable Settings pict.svg';
import SeamlessImage from '../assets/Seamless Management picot.svg';
import bgImage from '../assets/B.G.svg';
import surveyHosting from '../assets/Survey Hosting.jpg';
import StatisticalAnalysis from '../assets/Statistical Analysis.jpg';
import Academic from '../assets/Academic.png';
import Research from '../assets/Research Paper Support.jpg';
import languageImage from '../assets/Dual-Language Platform-pictogram.jpg';
import empoweringTextImage from '../assets/brand asset.svg';
import HeroVideo from '../assets/hero_section_video.mp4';
import wordBg from '../assets/brand asset.svg';
import dollarImage from '../assets/DollarImage.png';
import empoweringBgImage from '../assets/B.G.svg';

import './/Home.css'; // 👈 CSS file

function Home() {
  const { t, i18n } = useTranslation();
  const direction = i18n.language === 'ar' ? 'rtl' : 'ltr';
  const navigate = useNavigate();

  const containerRef = useRef(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  const scrollToService = (index) => {
    if (containerRef.current) {
      const container = containerRef.current;
      const serviceWidth = 447 + 32;
      container.scrollTo({
        left: index * serviceWidth,
        behavior: 'smooth',
      });
      setCurrentIndex(index);
    }
  };

  const handlePrev = () => {
    const newIndex = Math.max(currentIndex - 1, 0);
    scrollToService(newIndex);
  };

  const handleNext = () => {
    const newIndex = Math.min(currentIndex + 1, 1);
    scrollToService(newIndex);
  };

  return (
    <div className="home-page" style={{ direction }}>
      {/* HERO */}
      <section className="hero">
        <video autoPlay loop muted playsInline>
          <source src={HeroVideo} type="video/mp4" />
        </video>
        <div className="hero-content">
          <p>{t('Welcome to surveyInk')}</p>
          <h1>{t('Connecting Minds, One Survey At A Time')}</h1>
          <a href="/signup" className="btn-primary">
            {t('Get_Started') || 'Get Started'}
          </a>
        </div>
      </section>

      {/* FEATURES */}
      <section className="features">
        <p className="sub">{t('where data meets innovation.')}</p>
        <h2>{t('A Dashboard Designed for Clarity')}</h2>

        <div className="features-grid">
          <div className="feature-card">
            <img src={AnalysisImage} alt="Analytics" />
            <h3>{t("Intuitive Analytics")}</h3>
            <p>{t("Gain instant insights with charts and reports.")}</p>
          </div>
          <div className="feature-card">
            <img src={SeamlessImage} alt="Management" />
            <h3>Seamless Management</h3>
            <p>Effortlessly manage your survey respondents.</p>
          </div>
          <div className="feature-card">
            <img src={CustomizableImage} alt="Settings" />
            <h3>Customizable Settings</h3>
            <p>Tailor your dashboard to fit your needs.</p>
          </div>
        </div>
      </section>

      {/* SERVICES */}
      <section className="services" style={{ backgroundImage: `url(${bgImage})` }}>
        <button onClick={handlePrev} disabled={currentIndex === 0}>←</button>
        <button onClick={handleNext} disabled={currentIndex === 1}>→</button>

        <div className="services-container" ref={containerRef}>
          {[StatisticalAnalysis, Academic, surveyHosting, Research].map((img, i) => (
            <div className="service-card" key={i}>
              <img src={img} alt="service" />
              <div>
                <h3>Service {i + 1}</h3>
                <p>Description for service {i + 1}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* CURRENCIES */}
      <section className="currencies">
        <div className="text">
          <h2>Supported Currencies</h2>
          <p>
            We currently support transactions in
            <span> EGP & USD</span>
          </p>
          <div className="btns">
            <button className="btn-primary">More</button>
            <button className="btn-outline">Contact Us</button>
          </div>
        </div>
        <div className="img">
          <img src={dollarImage} alt="currencies" />
        </div>
      </section>

      {/* EMPOWERING */}
      <section className="empowering" style={{ backgroundImage: `url(${empoweringBgImage})` }}>
        <div className="text">
          <h2>Empowering Research in The Middle East</h2>
          <p>
            Survey Ink empowers Middle East researchers with verified
            participants, expert support, and bilingual tools...
          </p>
        </div>
        <div className="join">
          <button onClick={() => navigate('/signup')}>JOIN US</button>
        </div>
      </section>
    </div>
  );
}

export default Home;
