import React, { useState, useEffect, useRef } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { useTranslation } from "react-i18next";
import logo from "../assets/HomeLogo.png";
import profileIcon from "../assets/profile-circle_svgrepo.com.svg";

function Header() {
  const { t, i18n } = useTranslation();
  const navigate = useNavigate();
  const [hoveredLink, setHoveredLink] = useState(null);
  const [profileMenuOpen, setProfileMenuOpen] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const profileMenuRef = useRef(null);
  const location = useLocation();
  const isProfilePage = location.pathname.startsWith("/profile");

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  const handleLogout = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("access");
      const response = await fetch("http://127.0.0.1:8000/api/logout/", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.ok) {
        localStorage.removeItem("access");
        navigate("/login", { replace: true });
      } else {
        console.error("Logout failed");
      }
    } catch (error) {
      console.error("Logout error:", error);
    }
  };

  // Close profile menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (profileMenuRef.current && !profileMenuRef.current.contains(event.target)) {
        setProfileMenuOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const toggleProfileMenu = () => {
    setProfileMenuOpen((prev) => !prev);
  };

  const navLinks = [
    { to: "/", label: "Home" },
    { to: "/about", label: "About us" },
    { to: "/contact", label: "Contact us" },
  ];

  const authLinks = [
    { to: "/login", label: "Login" },
    { to: "/signup", label: "Sign Up" },
  ];

  const isAuthenticated = !!localStorage.getItem("access");

  return (
    <header
      className={`flex items-center justify-between h-[70px] px-5 z-50 ${
        isProfilePage ? "bg-transparent text-white" : "bg-white text-black"
      }`}
    >
      {/* Left - Logo */}
      <div className="flex-shrink-0">
        <img src={logo} alt="SURVEY INK Logo" className="h-[35px] w-[68px]" />
      </div>

      {/* Center - Navigation Links (hidden on mobile) */}
      <nav className="hidden md:flex gap-8">
        {navLinks.map((link, index) => (
          <Link
            key={`nav-${index}`}
            to={link.to}
            onMouseEnter={() => setHoveredLink(`nav-${index}`)}
            onMouseLeave={() => setHoveredLink(null)}
            className={`uppercase font-medium text-[16px] transition-all duration-300 ${
              hoveredLink === `nav-${index}` ? "border-b-2 border-black" : "border-none"
            }`}
          >
            {link.label}
          </Link>
        ))}
      </nav>

      {/* Right - Language + Profile or Auth */}
      <div className="flex items-center gap-4">
        <select
          onChange={(e) => changeLanguage(e.target.value)}
          value={i18n.language}
          className="bg-white text-black border border-black px-2 py-1 text-sm"
        >
          <option value="en">EN</option>
          <option value="ar">AR</option>
        </select>

        {isAuthenticated ? (
          <div className="relative" ref={profileMenuRef}>
            <img
              src={profileIcon}
              alt="Profile"
              className="w-10 h-10 cursor-pointer"
              onClick={toggleProfileMenu}
            />
            {profileMenuOpen && (
              <div className="absolute top-12 right-0 bg-white shadow-md rounded-md w-40 text-sm overflow-hidden">
                <Link
                  to="/profile"
                  className="block px-4 py-2 text-black hover:bg-gray-100 border-b"
                  onClick={() => setProfileMenuOpen(false)}
                >
                  Profile
                </Link>
                <button
                  onClick={(e) => {
                    setProfileMenuOpen(false);
                    handleLogout(e);
                  }}
                  className="w-full text-left px-4 py-2 hover:bg-gray-100 text-black"
                >
                  Logout
                </button>
              </div>
            )}
          </div>
        ) : (
          <div className="hidden md:flex gap-4">
            {authLinks.map((link, index) => (
              <Link
                key={`auth-${index}`}
                to={link.to}
                className="uppercase font-medium text-[16px] px-4 py-2 border border-transparent rounded hover:border-gray-400"
              >
                {link.label}
              </Link>
            ))}
          </div>
        )}
      </div>

      {/* Mobile Menu Button */}
      <button
        className="block md:hidden text-2xl"
        onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
      >
        â˜°
      </button>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <div className="absolute top-[70px] left-0 right-0 bg-white flex flex-col gap-4 p-5 md:hidden shadow-md">
          {navLinks.map((link, index) => (
            <Link
              key={`mnav-${index}`}
              to={link.to}
              onClick={() => setMobileMenuOpen(false)}
              className="font-medium text-[16px] text-black"
            >
              {link.label}
            </Link>
          ))}
          {!isAuthenticated &&
            authLinks.map((link, index) => (
              <Link
                key={`mauth-${index}`}
                to={link.to}
                onClick={() => setMobileMenuOpen(false)}
                className="font-medium text-[16px] text-black"
              >
                {link.label}
              </Link>
            ))}
        </div>
      )}
    </header>
  );
}

export default Header;
