import React, { useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

import AnalysisImage from '../assets/Intuitive Analytics picotgram.svg';
import CustomizableImage from '../assets/Customizable Settings pict.svg';
import SeamlessImage from '../assets/Seamless Management picot.svg';
import bgImage from '../assets/B.G.svg';
import surveyHosting from '../assets/Survey Hosting.jpg';
import StatisticalAnalysis from '../assets/Statistical Analysis.jpg';
import Academic from '../assets/Academic.png';
import Research from '../assets/Research Paper Support.jpg';
import languageImage from '../assets/Dual-Language Platform-pictogram.jpg';
import paymentImage from '../assets/Fair Compensation .jpg';
import verifyImage from '../assets/Verified Participants.jpg';
import responseImage from '../assets/One-Time Response Policy.jpg';
import empoweringTextImage from '../assets/brand asset.svg';
import HeroVideo from '../assets/hero_section_video.mp4';
import wordBg from '../assets/brand asset.svg';
import dollarImage from '../assets/DollarImage.png';
import empoweringBgImage from '../assets/B.G.svg';
import empoweringImage from '../assets/empoweringHome.png';

function Home() {
  const { t, i18n } = useTranslation();
  const direction = i18n.language === 'ar' ? 'rtl' : 'ltr';
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  const scrollToService = (index) => {
    if (containerRef.current) {
      const container = containerRef.current;
      const serviceWidth = 447 + 32;
      container.scrollTo({
        left: index * serviceWidth,
        behavior: 'smooth'
      });
      setCurrentIndex(index);
    }
  };

  const handlePrev = () => {
    const newIndex = Math.max(currentIndex - 1, 0);
    scrollToService(newIndex);
  };

  const handleNext = () => {
    const newIndex = Math.min(currentIndex + 1, 1);
    scrollToService(newIndex);
  };

  return (
    <div className="home-page" style={{ direction }}>
      {/* Hero Section */}
      <section className="relative h-screen overflow-hidden">
        <video
          autoPlay
          loop
          muted
          playsInline
          className="absolute top-0 left-0 w-full h-full object-cover -z-10"
        >
          <source src={HeroVideo} type="video/mp4" />
        </video>
        <div className="relative z-10 flex h-full flex-col justify-center px-6 md:px-12 text-white">
          <div className="max-w-2xl">
            <p className="text-lg opacity-90 mb-4">{t('Welcome to surveyInk')}</p>
            <h1 className="text-4xl md:text-6xl font-bold leading-tight text-[#6CAEDB] mb-6">
              {t('Connecting Minds, One Survey At A Time')}
            </h1>
            <a
              href="/signup"
              className="inline-block rounded-full bg-[#F19303] px-6 py-2 text-base font-semibold text-white hover:bg-[#d67e02] transition"
            >
              {t('Get_Started') || 'Get Started'}
            </a>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="mt-20 bg-white text-center">
        <div className="max-w-6xl mx-auto">
          <p className="text-[#F19303] text-lg mb-2">{t('where data meets innovation.')}</p>
          <h2 className="text-2xl md:text-3xl font-semibold mb-12">
            {t('A Dashboard Designed for Clarity')}
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-12">
            <div className="flex flex-col items-center gap-4">
              <img src={AnalysisImage} alt="Analytics icon" className="w-16 h-16" />
              <h3 className="text-xl font-semibold">{t('Intuitive Analytics')}</h3>
              <p className="text-gray-700">{t('Gain instant insights with charts and reports.')}</p>
            </div>
            <div className="flex flex-col items-center gap-4">
              <img src={SeamlessImage} alt="Management icon" className="w-20 h-20" />
              <h3 className="text-xl font-semibold">Seamless Management</h3>
              <p className="text-gray-700">Effortlessly manage your survey respondents.</p>
            </div>
            <div className="flex flex-col items-center gap-4">
              <img src={CustomizableImage} alt="Settings icon" className="w-20 h-20" />
              <h3 className="text-xl font-semibold">Customizable Settings</h3>
              <p className="text-gray-700">Tailor your dashboard to fit your needs.</p>
            </div>
          </div>
        </div>
      </section>

      {/* Services Section */}
      <section
        className="py-16 mt-12 relative bg-cover bg-center"
        style={{ backgroundImage: `url(${bgImage})` }}
      >
        <div className="max-w-7xl mx-auto relative">
          {/* Arrows */}
          <button
            onClick={handlePrev}
            disabled={currentIndex === 0}
            className="absolute -left-12 top-1/2 transform -translate-y-1/2 bg-white border border-gray-200 rounded-full w-10 h-10 flex items-center justify-center shadow disabled:opacity-50"
          >
            ←
          </button>
          <button
            onClick={handleNext}
            disabled={currentIndex === 1}
            className="absolute -right-12 top-1/2 transform -translate-y-1/2 bg-white border border-gray-200 rounded-full w-10 h-10 flex items-center justify-center shadow disabled:opacity-50"
          >
            →
          </button>

          {/* Carousel */}
          <div className="overflow-hidden">
            <div ref={containerRef} className="flex gap-8 w-full overflow-x-scroll no-scrollbar">
              {[{
                img: StatisticalAnalysis,
                title: 'Statistical Analysis',
                desc: 'Expert statistical analysis to help you interpret your data.'
              }, {
                img: Academic,
                title: 'Academic Publishing Help',
                desc: 'Guidance through the academic publishing process.'
              }, {
                img: surveyHosting,
                title: 'Survey Hosting',
                desc: 'Secure and reliable hosting for your surveys.'
              }, {
                img: Research,
                title: 'Data Collection',
                desc: 'High-quality data collection methods.'
              }].map((s, i) => (
                <div key={i} className="min-w-[300px] md:min-w-[447px] bg-white rounded-lg border shadow flex flex-col">
                  <div className="h-48 bg-gray-100">
                    <img src={s.img} alt={s.title} className="w-full h-full object-cover" />
                  </div>
                  <div className="p-6 text-center flex-1">
                    <h3 className="text-xl font-semibold text-gray-900 mb-2">{s.title}</h3>
                    <p className="text-gray-600">{s.desc}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Currency Support Section */}
      <section className="mt-20 bg-white py-12">
        <div className="max-w-6xl mx-auto flex flex-col md:flex-row items-center gap-8">
          <div className="flex-1 text-left">
            <h2 className="text-2xl md:text-3xl font-semibold text-[#395692] mb-4">Supported Currencies</h2>
            <p className="text-gray-800 mb-2">We currently support transactions in</p>
            <p className="text-[#F19303] font-medium mb-4">Egyptian Pound (EGP) & American Dollars (USD)</p>
            <div className="flex gap-4">
              <button className="px-4 py-2 rounded-lg bg-[#395692] text-white font-medium">More</button>
              <button className="px-4 py-2 rounded-lg border border-[#395692] text-[#395692] font-medium">Contact Us</button>
            </div>
          </div>
          <div className="flex-1 text-center">
            <img src={dollarImage} alt="Currency Support" className="max-w-xs mx-auto" />
          </div>
        </div>
      </section>

      {/* Empowering Section */}
      <section className="mt-20 bg-cover bg-center py-16" style={{ backgroundImage: `url(${empoweringBgImage})` }}>
        <div className="max-w-6xl mx-auto flex flex-col md:flex-row items-center gap-8">
          <div className="flex-1 text-left">
            <h2 className="text-2xl md:text-3xl font-semibold text-[#23407F] mb-4">
              Empowering Research in The Middle East
            </h2>
            <p className="text-gray-700 max-w-md">
              Survey Ink empowers Middle East researchers with verified participants, expert support, and bilingual tools for faster, affordable insights. Freelancers gain flexible, meaningful work by contributing to impactful academic, market, and thesis research projects.
            </p>
            <button
              onClick={() => navigate('/signup')}
              className="mt-6 rounded-full bg-[#F19303] text-white px-6 py-2 font-semibold shadow hover:bg-[#d67e02] transition"
            >
              JOIN US
            </button>
          </div>
          <div className="flex-1 hidden md:block">
            <img src={empoweringImage} alt="Empowering Illustration" className="w-full h-auto" />
          </div>
        </div>
      </section>
    </div>
  );
}

export default Home;
